let x = (new Bond).subscriptable();
let y = x.foo;
y.log(); // nothing yet
x.changed({foo: 42, bar: 69});	// logs 42

let x = (new Bond).defaultTo(42);
x.log();	// 42
x.changed(69);
x.log();	// 69
x.reset();
x.log() // 42

// These two expressions are exactly equivalent:
bond.notReady();
bond.ready().map(_ => !_);

let x = new Bond;
x.then(console.log);
x.changed(42); // 42 is written to the console.

let x = new Bond;
x.then(console.log);
x.changed(42); // 42 is written to the console.

let b = new Bond;
let t = b.map(_ => _ * 2);
t.tie(console.log);
b.changed(21); // logs 42
b.changed(34.5); // logs 69

