let b = new Bond;
let c = new Bond;
let t = Bond.all(['a', {b, c}, 'd'], 2);
t.tie(console.log);
b.changed(42);
c.changed(69); // logs ['a', {b: 42, c: 69}, 'd']
b.changed(null); // logs ['a', {b: null, c: 69}, 'd']

