const { Bond } = require('oo7')
const WebSocket = require('isomorphic-ws')

const subscriptionKey = {
	author_submitAndWatchExtrinsic: {
		notification: 'author_extrinsicUpdate',
		subscribe: 'author_submitAndWatchExtrinsic',
		unsubscribe: 'author_unwatchExtrinsic'
	},
	state_storage: {
		notification: 'state_storage',
		subscribe: 'state_subscribeStorage',
		unsubscribe: 'state_unsubscribeStorage'
	},
	chain_newHead: {
		notification: 'chain_newHead',
		subscribe: 'chain_subscribeNewHead',
		unsubscribe: 'chain_unsubscribeNewHead'
	},
	chain_finalisedHead: {
		notification: 'chain_finalisedHead',
		subscribe: 'chain_subscribeFinalisedHeads',
		unsubscribe: 'chain_unsubscribeFinalisedHeads'
	},
	state_runtimeVersion: {
		notification: 'state_runtimeVersion',
		subscribe: 'state_subscribeRuntimeVersion',
		unsubscribe: 'state_unsubscribeRuntimeVersion'
	}
}

let uri = ['ws://127.0.0.1:9944']

function setNodeUri(u) {
	if (uri === u) return
	uri = u
	if (!s_nodeService) return // prevent instanciating
	s_nodeService.uri = u
	s_nodeService.uriIndex = 0
	s_nodeService.uriChanged = true
	s_nodeService.start()
}

class NodeService {
	constructor (uri) {
		this.subscriptions = {}
		this.cancelations = {}
		this.pendingCancelations = {}
		this.theirIds = {}
		this.onReply = {}
		this.onceOpen = []
		this.index = 1
		this.uriIndex = 0
		this.backoff = 0
		this.uri = uri
		this.status = new Bond
		this.start(uri[0])
	}

	start (uri = this.uri[0]) {
		if (this.ws) {
			this.ws.close()
			delete this.ws
		}

		let that = this
		this.ws = new WebSocket(uri)
		this.ws.onopen = function () {
			console.log('Connection open')
			that.rejig()
			that.backoff = 0
			let onceOpen = that.onceOpen;
			that.onceOpen = []
			setTimeout(() => {
//				console.warn("Proceessing deferred requests...")
				onceOpen.forEach(f => f())
			}, 0)
			that.status.trigger({connected: uri})
		}
		this.ws.onmessage = function (msg) {
			if (that.reconnect) {
				clearTimeout(that.reconnect)
			}

			let d = JSON.parse(msg.data)
//			console.log('Incoming:', d);
			if (d.id) {
				that.onReply[d.id](d)
				delete that.onReply[d.id];
			} else if (d.method && d.params && that.subscriptions[d.params.subscription]) {
				that.subscriptions[d.params.subscription].callback(d.params.result, d.method)
			} else if (that.pendingCancelations[d.params.subscription]) {
				// Ok; this message was sent by them before they heard that we wanted to unsubscribe.
			} else {
				console.warn("Subscription reply without recognised ID", d.params.subscription)
			}

			// epect a message every 10 seconds or we reconnect.
			that.reconnect = setTimeout(() => { console.log('Reconnecting.'); that.start() }, 60000)
		}
		this.ws.onerror = (err) => {
			if (that.uriChanged) {
				delete that.uriChanged
				return // no reconnection if uri changed
			}
			setTimeout(() => {
				that.uriIndex = (that.uriIndex + 1) % that.uri.length
				that.start(that.uri[that.uriIndex])
			}, that.backoff)
			that.backoff = Math.min(30000, that.backoff + 1000)
			that.status.trigger({error: err})
		}
	}

	rejig () {
		let that = this
		let subs = this.subscriptions
		this.subscriptions = {}
		let theirIds = this.theirIds
		this.theirIds = {}
		Object.keys(theirIds).forEach(ourId => {
			let sub = subs[theirIds[ourId]]
			that.subscribe(sub.what, sub.params, sub.callback, console.warn, ourId)
		})
	}

	req (method, params, callback) {
		let that = this
		let doSend = () => {
			let id = '' + this.index++;
//			console.warn("Executing request", method, params, id, callback)
			let msg = {
				"jsonrpc": "2.0",
				"id": id,
				"method": method,
				"params": params
			};
			that.ws.send(JSON.stringify(msg))

			that.onReply[id] = callback
		}

		if (this.ws.readyState === 1) {
			doSend(callback)
		} else {
//			console.warn("Defering request until connected", method, params)
			that.onceOpen.push(() => {
				doSend(callback)
			})
		}
	}

	request (method, params = []) {
		let that = this
		return new Promise((resolve, reject) => {
			that.req(method, params, msg => {
//				console.warn("Processing request reply", method, params, msg)
				if (msg.error) {
					reject(msg.error)
				} else {
					resolve(msg.result)
				}
			})
		})
	}

	subscribe (what, params, callback, errorHandler, ourId = null) {
		let that = this
		return new Promise((resolve, reject) => {
//			console.log('Subscribing', ourId)
			this.req(subscriptionKey[what].subscribe, params, msg => {
				if (msg.error) {
//					console.log('Error subscribing', ourId)
					errorHandler(msg.error)
				} else {
					let theirId = msg.result
//					console.log('Subscribed', 'ourId=', ourId, 'theirId=', theirId)
					if (that.cancelations[ourId]) {
//						console.log('Delayed unsubscription of', ourId)
						that.pendingCancelations[theirId] = ourId
						this.req(subscriptionKey[what].unsubscribe, [theirId], () => {
							delete that.pendingCancelations[theirId]
							delete that.cancelations[ourId]
						}, errorHandler)
					} else {
						that.subscriptions[theirId] = { what, params, callback }
						ourId = ourId || theirId
						that.theirIds[ourId] = theirId
					}
					// We resolve to our ID regardless which should be safe since
					// unsubscribes of old IDs are no-ops.
					resolve(ourId)
				}
			})
		})
	}

	unsubscribe (ourId) {
		let that = this

		if (this.theirIds[ourId] == null) {
//			console.log('Resubscription not yet complete. Defering unsubscribe', ourId)
			this.cancelations[ourId] = true
			return
		}
		let theirId = this.theirIds[ourId]
		if (!this.subscriptions[theirId]) {
			throw 'Invalid subscription id'
		}
		let unsubscribe = subscriptionKey[this.subscriptions[theirId].what].unsubscribe

//		console.log('Unsubscribing', ourId, theirId, this.subscriptions[theirId].what, unsubscribe)
		this.req(unsubscribe, [theirId], () => {
			delete that.theirIds[ourId]
			delete that.subscriptions[theirId]
		})
	}

	finalise () {
		delete this.ws;
	}
}

let s_nodeService = null;

function nodeService() {
	if (!s_nodeService) {
		s_nodeService = new NodeService(uri);
	}
	return s_nodeService;
}

module.exports = { nodeService, NodeService, setNodeUri };

const { ss58Encode } = require('./ss58')
const { bytesToHex } = require('./utils')
const { VecU8, AccountId, Hash, SlashPreference, VoteThreshold, Moment, Balance, BlockNumber, AccountIndex, Tuple, reviver } = require('./types')
const { denominationInfo } = require('./denominationInfo')

const numberWithCommas = n => {
	let x = n.toString();
	if (x.indexOf('.') > -1) {
		let [a, b] = x.split('.');
		return numberWithCommas(a) + '.' + b;
	} else {
		return x.replace(/\B(?=(\d{3})+(?!\d))/g, ",");
	}
}

//TODO: AccountIndex

function pretty(expr) {
	if (expr === null) {
		return 'null';
	}
	if (expr instanceof VoteThreshold) {
		return 'VoteThreshold.' + expr;
	}
	if (expr instanceof SlashPreference) {
		return 'SlashPreference{unstake_threshold=' + expr + '}';
	}
	if (expr instanceof Balance) {
		let di = denominationInfo

		let denomincationSearch = [di.primary, Object.keys(di.denominations)]
		let unit = null
		let dp = 0
		for (ii in denomincationSearch) {
			let i = denomincationSearch[ii]
			let denom = di.denominations[i]
			let divisor = Math.pow(10, denom)
			let lower = divisor / 30
			let upper = divisor * 30000
			if (expr > lower && expr < upper) {
				unit = i
				expr /= divisor
				for (; expr < 3000 / Math.pow(10, dp); dp++) {}
				break;
			}
		}

		if (unit === null) {
			// default
			if (expr < Math.pow(10, di.denominations[di.primary]) / 30 && expr !== 0) {
				unit = di.unit
			} else {
				unit = di.primary
				expr /= Math.pow(10, di.denominations[unit])
				expr = Math.round(expr)
			}
		}

		return numberWithCommas(Math.round(expr * Math.pow(10, dp)) / Math.pow(10, dp)) + ' ' + unit
	}
	if (expr instanceof BlockNumber) {
		return numberWithCommas(expr);
	}
	if (expr instanceof Hash) {
		return '0x' + bytesToHex(expr);
	}
	if (expr instanceof Moment) {
		return expr.toLocaleString() + " (" + expr.number + " seconds)";
	}
	if (expr instanceof AccountId) {
		return ss58Encode(expr);
	}
	if (expr instanceof Tuple) {
		return '(' + expr.map(pretty).join(', ') + ')';
	}
	if (expr instanceof VecU8 || expr instanceof Uint8Array) {
		if (expr.length <= 256) {
			return '[' + bytesToHex(expr) + ']';
		} else {
			return `[${bytesToHex(expr.slice(0, 256))}...] (${expr.length} bytes)`;
		}
	}
	if (expr instanceof Array) {
		return '[' + expr.map(pretty).join(', ') + ']';
	}
	if (typeof expr === 'object') {
		return '{' + Object.keys(expr).map(k => k + ': ' + pretty(expr[k])).join(', ') + '}';
	}
	return '' + expr;
}

module.exports = { pretty };

const { Bond } = require('oo7')
const nacl = require('tweetnacl');
const { generateMnemonic, mnemonicToSeed } = require('bip39')
const { ss58Encode } = require('./ss58')
const { AccountId } = require('./types')
const { bytesToHex, hexToBytes } = require('./utils')

let cache = {}

function seedFromPhrase(phrase) {
	if (!cache[phrase]) {
		cache[phrase] = phrase.match(/^0x[0-9a-fA-F]{64}$/)
			? hexToBytes(phrase)
			: new Uint8Array(mnemonicToSeed(phrase).slice(0, 32))
	}
	return cache[phrase]
}

class SecretStore extends Bond {
	constructor (storage) {
		super()
		this._storage = storage || typeof localStorage === 'undefined' ? {} : localStorage
		this._keys = []
		this._load()
	}

	submit (phrase, name) {
		this._keys.push({phrase, name})
		this._sync()
		return this.accountFromPhrase(phrase)
	}

	accountFromPhrase (phrase) {
		return new AccountId(nacl.sign.keyPair.fromSeed(seedFromPhrase(phrase)).publicKey)
	}

	accounts () {
		return this._keys.map(k => k.account)
	}

	find (identifier) {
		if (this._keys.indexOf(identifier) !== -1) {
			return identifier
		}
		if (identifier instanceof Uint8Array && identifier.length == 32 || identifier instanceof AccountId) {
			identifier = ss58Encode(identifier)
		}
		return this._byAddress[identifier] ? this._byAddress[identifier] : this._byName[identifier]
	}

	sign (from, data) {
		let item = this.find(from)
		if (item) {
			console.info(`Signing data from ${item.name}`, bytesToHex(data))
			let sig = nacl.sign.detached(data, item.key.secretKey)
			console.info(`Signature is ${bytesToHex(sig)}`)
			if (!nacl.sign.detached.verify(data, sig, item.key.publicKey)) {
				console.warn(`Signature is INVALID!`)
				return null
			}
			return sig
		}
		return null
	}

	forget (identifier) {
		let item = this.find(identifier)
		if (item) {
			console.info(`Forgetting key ${item.name} (${item.address}, ${item.phrase})`)
			this._keys = this._keys.filter(i => i !== item)
			this._sync()
		}
	}

	_load () {
		if (this._storage.secretStore) {
			this._keys = JSON.parse(this._storage.secretStore).map(({seed, phrase, name}) => ({ phrase, name, seed: hexToBytes(seed) }))
		} else if (this._storage.secretStore2) {
			this._keys = JSON.parse(this._storage.secretStore2).map(({seed, name}) => ({ phrase: seed, name }))
		} else {
			this._keys = [{
				name: 'Default',
				phrase: generateMnemonic()
			}]
		}
		this._sync()
	}

	_sync () {
		let byAddress = {}
		let byName = {}
		this._keys = this._keys.map(({seed, phrase, name, key}) => {
			seed = seed || seedFromPhrase(phrase)
			key = key || nacl.sign.keyPair.fromSeed(seed)
			let account = new AccountId(key.publicKey)
			let address = ss58Encode(account)
			let item = {seed, phrase, name, key, account, address}
			byAddress[address] = item
			byName[name] = item
			return item
		})
		this._byAddress = byAddress
		this._byName = byName
		this._storage.secretStore = JSON.stringify(this._keys.map(k => ({seed: bytesToHex(k.seed), phrase: k.phrase, name: k.name})))
		this.trigger({keys: this._keys, byAddress: this._byAddress, byName: this._byName})
	}
}

let s_secretStore = null;

function secretStore(storage) {
	if (s_secretStore === null) {
		s_secretStore = new SecretStore(storage);
	}
	return s_secretStore;
}

module.exports = { secretStore, SecretStore };

