const { Bond, TransformBond } = require('oo7')
const { ss58Encode } = require('../ss58')
const { Balance } = require('../types')
const balancesModule = require('./balances')
const sessionModule = require('./session')

function compareAccountId(a, b) {
	return a.length === b.length && a.every((v, i) => b[i] === v)
}

function accountIdMemberOf(member, set) {
	return set.find(item => compareAccountId(member, item)) !== undefined
}

function augment (runtime, chain) {
	sessionModule.augment(runtime, chain)
	balancesModule.augment(runtime, chain)
	let session = runtime.session
	let staking = runtime.staking
	let balances = runtime.balances
	if (staking._extras) {
		return
	} else {
		staking._extras = true
	}

	staking.thisSessionReward = new TransformBond(
		(r, l) => Math.round(r / l),
		[
			staking.sessionReward,
			session.lateness
		]
	)

	staking.currentNominatedBalance = who => staking.currentNominatorsFor(who)
		.map(ns => ns.map(n => balances.totalBalance(n)), 2)
		.map(bs => new Balance(bs.reduce((a, b) => a + b, 0)))
	staking.nominatedBalance = who => staking.nominatorsFor(who)
		.map(ns => ns.map(n => balances.totalBalance(n)), 2)
		.map(bs => new Balance(bs.reduce((a, b) => a + b, 0)))
	staking.stakingBalance = who => Bond
		.all([balances.totalBalance(who), staking.nominatedBalance(who)])
		.map(([f, r]) => new Balance(f + r));
	staking.currentStakingBalance = who => Bond
		.all([balances.totalBalance(who), staking.currentNominatedBalance(who)])
		.map(([f, r]) => new Balance(f + r));

	staking.eraLength = new TransformBond(
		(a, b) => a * b,
		[
			staking.sessionsPerEra,
			session.sessionLength
		])

	staking.validators = Bond.all([staking.invulerables, session.validators])
		.map(([inv, v]) => v.map(who => ({
			who,
			ownBalance: balances.totalBalance(who),
			otherBalance: staking.currentNominatedBalance(who),
			nominators: staking.currentNominatorsFor(who),
			invulnerable: accountIdMemberOf(who, inv)
		})), 2)
		.map(v => v
			.map(i => Object.assign({balance: i.ownBalance.add(i.otherBalance)}, i))
			.sort((a, b) => b.balance - a.balance)
		)

	staking.nextThreeUp = staking.intentions.map(
			l => ([session.validators, l.map(who => ({
				who, ownBalance: balances.totalBalance(who), otherBalance: staking.nominatedBalance(who)
			}) ) ]), 3
		).map(([c, l]) => l
			.map(i => Object.assign({balance: i.ownBalance.add(i.otherBalance)}, i))
			.sort((a, b) => b.balance - a.balance)
			.filter(i => !c.some(x => x+'' == i.who+''))
			.slice(0, 3)
		)

	staking.nextValidators = Bond
		.all([
			staking.intentions.map(v => v.map(who => ({
				who,
				ownBalance: balances.totalBalance(who),
				otherBalance: staking.nominatedBalance(who),
				nominators: staking.nominatorsFor(who)
			})), 2),
			staking.validatorCount
		]).map(([as, vc]) => as
			.map(i => Object.assign({balance: i.ownBalance.add(i.otherBalance)}, i))
			.sort((a, b) => b.balance - a.balance)
			.slice(0, vc)
		)

	staking.eraSessionsRemaining = new TransformBond(
		(spe, si, lec) => (spe - 1 - (si - lec) % spe),
		[
			staking.sessionsPerEra,
			session.currentIndex,
			staking.lastEraLengthChange
		])

	staking.eraBlocksRemaining = new TransformBond(
		(sl, sr, br) => br + sl * sr, 
		[
			session.sessionLength,
			staking.eraSessionsRemaining,
			session.blocksRemaining
		])

	staking.intentionIndexOf = id =>
		new TransformBond((i, id) => {
			let ss58 = ss58Encode(id);
			return i.findIndex(a => ss58Encode(a) === ss58);
		}, [runtime.staking.intentions, id])

	staking.bondageOf = id =>
		new TransformBond(
			(b, h) => h >= b ? null : (b - h),
			[runtime.staking.bondage(id), chain.height]
		)

	staking.nominationIndex = (val) =>
		new TransformBond((i, id) => {
			let ss58 = ss58Encode(id);
			return i.findIndex(a => ss58Encode(a) === ss58);
		}, [runtime.staking.nominatorsFor(staking.nominating(val)), val])
}

module.exports = { augment }

const bs58 = require('bs58')
const { blake2b } = require('blakejs')
const { toLE, leToNumber } = require('./utils')
const { AccountIndex, AccountId } = require('./types')

let defaultType = 42
const KNOWN_TYPES = [0, 1, 42, 43, 68, 69]

function setNetworkDefault(type) {
	defaultType = type
}

function ss58Encode(a, type = defaultType, checksumLength = null, length = null, accountId) {
	let payload
	if (KNOWN_TYPES.indexOf(type) === -1) {
		throw new Error('Unknown ss58 address type', type)
	}
	if (typeof a === 'number' || a instanceof AccountIndex) {
		let minLength = (a < (1 << 8) ? 1 : a < (1 << 16) ? 2 : a < (1 << 32) ? 4 : 8)
		length = length ? length : minLength
		if ([1, 2, 4, 8].indexOf(length) === -1) {
			throw new Error('Invalid length')
		}
		length = Math.max(minLength, length)
		if (checksumLength && typeof checksumLength !== 'number') {
			throw new Error('Invalid checksum length')
		}
		switch (length) {
			case 1: { checksumLength = 1; break; }
			case 2: { checksumLength = ([1, 2].indexOf(checksumLength) + 1) || 1; break; }
			case 4: { checksumLength = ([1, 2, 3, 4].indexOf(checksumLength) + 1) || 1; break; }
			case 8: { checksumLength = ([1, 2, 3, 4, 5, 6, 7, 8].indexOf(checksumLength) + 1) || 1; break; }
		}
		payload = toLE(a, length)
	} else if ((a instanceof AccountId || a instanceof Uint8Array) && a.length === 32) {
		checksumLength = 2
		payload = a
		accountId = a
	} else {
		throw new Error('Unknown item to encode as ss58. Passing back.', a)
	}
	let hash = blake2b((type & 1) ? accountId : new Uint8Array([type, ...payload]))
	let complete = new Uint8Array([type, ...payload, ...hash.slice(0, checksumLength)])
	return bs58.encode(Buffer.from(complete))
}

/// `lookupIndex` must be synchronous. If you can do that, then throw, catch outside the
/// invocation and then retry once you have the result to hand.
function ss58Decode(ss58, lookupIndex) {
	let a
	try {
		a = bs58.decode(ss58)
	}
	catch (e) {
		return null
	}

	let type = a[0]
	if (KNOWN_TYPES.indexOf(type) === -1) {
		return null
	}

	if (a.length < 3) {
		return null
		//throw new Error('Invalid length of payload for address', a.length)
	}
	let length = a.length <= 3
		? 1
		: a.length <= 5
		? 2
		: a.length <= 9
		? 4
		: a.length <= 17
		? 8
		: 32
	let checksumLength = a.length - 1 - length

	let payload = a.slice(1, 1 + length)
	let checksum = a.slice(1 + a.length)

	let accountId
	if (length === 32) {
		accountId = payload
	}

	let result = length < 32
		? new AccountIndex(leToNumber(payload))
		: new AccountId(payload)

	if (a[0] % 1 && !accountId && !lookupIndex) {
		return null
	}
	let hash = blake2b(a[0] % 1 ? (accountId || lookupIndex(result)) : a.slice(0, 1 + length))

	for (var i = 0; i < checksumLength; ++i) {
		if (hash[i] !== a[1 + length + i]) {
			// invalid checksum
			return null
		}
	}

	return result
}

module.exports = { ss58Decode, ss58Encode, setNetworkDefault }

